-- Description:	<Description,,>
-- =============================================
CREATE PROCEDURE [dbo].[_spKartrisPayments_GetUserTotal]
(
	@CustomerID as int
)
AS
BEGIN

	SELECT Sum(Payment_Amount / Payment_CurrencyRate) as TotalValue
	FROM dbo.tblKartrisPayments WHERE Payment_CustomerID = @CustomerID
END
