CREATE PROCEDURE [dbo].[_spKartrisPayments_Add]
(
		   @Payment_CustomerID int,
		   @Payment_Date smalldatetime,
		   @Payment_Amount real,
		   @Payment_CurrencyID tinyint,
		   @Payment_ReferenceNo nvarchar(100),
		   @Payment_GateWay nvarchar(20),
		   @Payment_CurrencyRate real
)
AS
BEGIN
	DECLARE @Payment_ID INT
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT OFF;

	
	INSERT INTO [tblKartrisPayments]
		   ([Payment_CustomerID]
		   ,[Payment_Date]
		   ,[Payment_Amount]
		   ,[Payment_CurrencyID]
		   ,[Payment_ReferenceNo]
		   ,[Payment_Gateway]
		   ,[Payment_CurrencyRate]
		   )
	 VALUES
		   (@Payment_CustomerID,
		   @Payment_Date,
		   @Payment_Amount,
		   @Payment_CurrencyID,
		   @Payment_ReferenceNo,
		   @Payment_GateWay,
		   @Payment_CurrencyRate)

	SET @Payment_ID = SCOPE_IDENTITY();
	SELECT @Payment_ID;
	
END
