	@NowOffset as datetime,
	@TIC_Status as char,
	@TIC_TimeSpent as int,
	@TIC_Tags as nvarchar(200),
	@STT_ID as smallint
)
	
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
