					  SUM(tblKartrisSupportTickets.TIC_TimeSpent) AS UserTime, @WholeTickets AS TotalTickets, @WholeMessages AS TotalMessages, @WholeTime AS TotalTime
FROM         tblKartrisSupportTickets LEFT OUTER JOIN
