(
	@StartDate as datetime,
	@EndDate as datetime,
	@NoOfKeywords as int
)
AS
BEGIN
	SET NOCOUNT ON;

	SELECT Top(@NoOfKeywords) SS_Keyword, Sum(SS_Searches) As TotalSearches
