-- =============================================
CREATE PROCEDURE [dbo].[spKartrisTaxRates_GetClosestRate] ( 
	@computedTaxRate real
) AS
BEGIN
	SET NOCOUNT ON;
	select TOP 1 T_TaxRate from tblKartrisTaxRates ORDER BY ABS(T_TaxRate - @computedTaxRate), T_ID ASC
END
