-- ===============================================================
CREATE PROCEDURE [dbo].[_spKartrisDestinations_GetISOForFilter]
AS
BEGIN
	SET NOCOUNT ON;
	SELECT D_ISOCode
	FROM (	SELECT	DISTINCT D_ISOCode, COUNT(1) AS NoOfRecords
