/****** Object:  UserDefinedFunction [dbo].[fnKartrisProduct_GetMinPrice]    Script Date: 01/23/2013 21:59:11 ******/
SET ANSI_NULLS ON
GO
SET QUOTED_IDENTIFIER ON
GO
-- =============================================
-- Author:		Mohammad
-- Create date: <Create Date, ,>
-- Description:	<Description, ,>
-- =============================================
CREATE FUNCTION [dbo].[fnKartrisProduct_GetMinPrice] 
(
	-- Add the parameters for the function here
	@V_ProductID as int
	
)
RETURNS real
AS
BEGIN
	-- Declare the return variable here
	DECLARE @Result real;

	-- Add the T-SQL statements to compute the return value here
	SELECT @Result = Min(V_Price) FROM tblKartrisVersions WHERE V_ProductID = @V_ProductID AND V_Live = 1 AND tblKartrisVersions.V_CustomerGroupID IS NULL;
	
	DECLARE @QD_MinPrice as real;
	SELECT @QD_MinPrice = Min(QD_Price)
	FROM dbo.tblKartrisQuantityDiscounts INNER JOIN tblKartrisVersions 
		ON tblKartrisQuantityDiscounts.QD_VersionID = tblKartrisVersions.V_ID
	WHERE tblKartrisVersions.V_Live = 1 AND tblKartrisVersions.V_ProductID = @V_ProductID AND tblKartrisVersions.V_CustomerGroupID IS NULL;

	IF @QD_MinPrice <> 0 AND @QD_MinPrice IS NOT NULL AND @QD_MinPrice < @Result
	BEGIN
		SET @Result = @QD_MinPrice
	END

	IF @Result IS NULL
	BEGIN
		SET @Result = 0;
	END
	-- Return the result of the function
	RETURN @Result

END
GO

/****** Object:  UserDefinedFunction [dbo].[fnKartrisProduct_GetMaxPrice]    Script Date: 03/05/2017 12:27:37 ******/
SET ANSI_NULLS ON
GO
SET QUOTED_IDENTIFIER ON
GO
-- =============================================
-- Author:		Mohammad
-- Create date: <Create Date, ,>
-- Description:	<Description, ,>
-- =============================================
CREATE FUNCTION [dbo].[fnKartrisProduct_GetMaxPrice] 
(
	-- Add the parameters for the function here
	@V_ProductID as int
	
)
RETURNS real
AS
BEGIN
	-- Declare the return variable here
	DECLARE @Result decimal(18,4);

	-- Declare version and option parts of price
	DECLARE @VersionPrice decimal(18,4);
	DECLARE @OptionsPrice decimal(18,4);

	-- Get version price
	SELECT @VersionPrice = Max(V_Price) 
	FROM tblKartrisVersions INNER JOIN [dbo].[tblKartrisTaxRates]
			ON [V_Tax] = [T_ID]
	WHERE V_ProductID = @V_ProductID AND V_Live = 1 AND tblKartrisVersions.V_CustomerGroupID IS NULL;

	-- Get options price
	SELECT @OptionsPrice = Sum(T.MaxPriceChange) FROM
	(SELECT P_OPT_ProductID, OPTG_ID, Max(P_OPT_PriceChange) As MaxPriceChange FROM dbo.tblKartrisOptionGroups INNER JOIN
						 dbo.tblKartrisOptions ON dbo.tblKartrisOptionGroups.OPTG_ID = dbo.tblKartrisOptions.OPT_OptionGroupID INNER JOIN
						 dbo.tblKartrisProductOptionGroupLink ON dbo.tblKartrisOptionGroups.OPTG_ID = dbo.tblKartrisProductOptionGroupLink.P_OPTG_OptionGroupID INNER JOIN
						 dbo.tblKartrisProductOptionLink ON dbo.tblKartrisOptions.OPT_ID = dbo.tblKartrisProductOptionLink.P_OPT_OptionID 
	WHERE P_OPT_ProductID = @V_ProductID
	GROUP BY P_OPT_ProductID, OPTG_ID) As T

	IF @VersionPrice IS NULL
	BEGIN
		SET @VersionPrice = 0;
	END
	IF @OptionsPrice IS NULL
	BEGIN
		SET @OptionsPrice = 0;
	END

	SET @Result = @VersionPrice + @OptionsPrice

	-- Return the result of the function
	RETURN @Result

END

GO

/****** Object:  UserDefinedFunction [dbo].[fnKartrisLanguageElement_GetProductID]    Script Date: 01/23/2013 21:59:11 ******/
SET ANSI_NULLS ON
GO
SET QUOTED_IDENTIFIER ON
GO
-- =============================================
-- Author:		Mohammad
-- Create date: <Create Date, ,>
-- Description:	<Description, ,>
-- =============================================
CREATE FUNCTION [dbo].[fnKartrisLanguageElement_GetProductID] 
(
	-- Add the parameters for the function here
	@LE_TypeID as tinyint,
	@LE_ParentID as int
	
)
RETURNS int
AS
BEGIN
	-- Declare the return variable here
	DECLARE @Result int;
	SET @Result = 0;
	
	IF @LE_TypeID = 1	BEGIN SELECT @Result = V_ProductID FROM tblKartrisVersions WHERE V_ID = @LE_ParentID; END
	IF @LE_TypeID = 2	BEGIN SET @Result = @LE_ParentID; END
	IF @LE_TypeID = 14	BEGIN SELECT @Result = ATTRIBV_ProductID FROM tblKartrisAttributeValues WHERE ATTRIBV_ID = @LE_ParentID; END
	
	-- Return the result of the function
	RETURN @Result

END
GO
/****** Object:  StoredProcedure [dbo].[_spKartrisStatistics_GetProductStatsDetailsByDate]    Script Date: 01/23/2013 21:59:10 ******/
SET ANSI_NULLS ON
GO
SET QUOTED_IDENTIFIER ON
GO
-- =============================================
-- Author:		Mohammad
-- Create date: <Create Date,,>
-- Description:	<Description,,>
-- =============================================
CREATE PROCEDURE [dbo].[_spKartrisStatistics_GetProductStatsDetailsByDate]
(
	@ProductID as int,
	@Month as int,
	@Year as int,
	@LANG_ID as tinyint
)
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;

	SELECT     COUNT(ST_ID) AS NoOfHits, 
	ST_ItemParentID AS CategoryID, dbo.fnKartrisLanguageElement_GetItemValue(@LANG_ID, 3, 1, ST_ItemParentID) AS CategoryName
	FROM         tblKartrisStatistics
	WHERE     (ST_Type = 'P') AND (ST_ItemID = @ProductID) AND
				(MONTH(ST_Date) = @Month) AND (YEAR(ST_Date) = @Year)
	GROUP BY ST_ItemID, ST_ItemParentID
	ORDER BY NoOfHits DESC

END
GO
