/****** Object:  StoredProcedure [dbo].[_spKartrisProductOptionLink_GetByProductID]    Script Date: 01/23/2013 21:59:10 ******/
SET ANSI_NULLS ON
GO
SET QUOTED_IDENTIFIER ON
GO
CREATE PROCEDURE [dbo].[_spKartrisProductOptionLink_GetByProductID]
(
	@ProductID int
)
