CREATE PROCEDURE [dbo].[_spKartrisVersions_Get]
(
	@LANG_ID tinyint
)
AS
	SET NOCOUNT ON;
SELECT        vKartrisTypeVersions.V_ID, vKartrisTypeVersions.V_Name
FROM            vKartrisTypeVersions
WHERE        (LANG_ID = @LANG_ID) AND V_Type <> 'c' AND V_Type <> 's'
